mapper.select:

SELECT	COUNT(*)
FROM	ef_center
WHERE	ef_c_name = #{ef_c_name}


SELECT	COUNT(*)
FROM	ef_member
WHERE	ef_id = #{ef_id}


SELECT ef_id
FROM   ef_member
WHERE  ef_name = #{ef_name}
AND    ef_p_number = #{ef_p_number}


SELECT	ef_pwd
FROM	ef_member
WHERE	ef_p_number = #{ef_p_number}
AND	    ef_id = #{ef_id}


SELECT	*
FROM	ef_member
WHERE	ef_id = #{ef_id}
AND	    ef_pwd = #{ef_pwd}


select * from ef_member 
where  ef_c_name = #{ef_c_name}
and    ef_class = '02'


select * from ef_member 
where  ef_c_name = #{ef_c_name}
and    ef_class = '03'


select * 
from   ef_member
where  ef_id = #{ef_id}
and    ef_pwd = #{ef_pwd}

mapper.update:

	update ef_member
	set    
		   ef_pwd = #{ef_pwd}, ef_name = #{ef_name}, 
		   ef_p_number = #{ef_p_number}
    where  ef_id = #{ef_id}

mapper.select:

select * from ef_member 
where  ef_c_name = #{ef_c_name}
and    ef_class = '12'

mapper.update:

update ef_m_regi
set    ef_p_type = #{ef_p_type}, ef_count = #{ef_count},
	   ef_start_term = #{ef_start_term}, ef_end_term = #{ef_end_term}
where  ef_id = #{ef_id}

mapper.delete:

delete from ef_pt
where ef_id = #{ef_id}
and ef_p_date = #{ef_p_date}
and ef_p_time = #{ef_p_time}
and ef_r_personal = '0'

mapper.update:

update  ef_pt
set		ef_p_personal = #{ef_p_personal}
where	ef_id = #{ef_id}
and ef_p_date = #{ef_p_date}
and ef_p_time = #{ef_p_time}

mapper.select:

select * 
from reservation
where ef_p_no = #{ef_p_no}


select *
from   ef_m_regi
where  ef_id = #{ef_id}


select *
from   ef_pt
where  ef_c_name = #{ef_c_name}
and    ef_p_type = #{ef_p_type}
and    ef_p_date >= sysdate-1
order by ef_p_date, ef_p_time


select *
from   ef_pt
where  ef_c_name = #{ef_c_name}


select count(*)
from   reservation
where  ef_p_no = #{ef_p_no}
and    ef_id   = #{ef_id}

mapper.delete:

delete from reservation
where ef_id = #{ef_id}
and ef_p_no = #{ef_p_no}

mapper.update:

update  ef_pt
set		ef_r_personal = ef_r_personal - 1
where	ef_p_no = #{ef_p_no}


update  ef_m_regi
set		ef_count = ef_count + 1
where	ef_id = #{ef_id}
